      fetch (`https://hacker-news.firebaseio.com/v0/item/${itemId}.json?print=pretty`)
      const storyInfo = await api_story_id.json()
      itemGet.push(storyInfo);
      by= storyInfo.by
      // setItemm(storyInfo)
      console.log(by)
      console.log(storyInfo.url)
      console.log(storyInfo.title)
    }
    fetchStory(console.log(by))
    // setItemmm(itemGett)
    // setIsLoadingg(false)
    console.log(by)
  // }, [queryy]);

  const result = itemGet.flatMap((item) => {
    return <p key={item.id}>
       {item.by} {item.title} 
    </p>;
 });

  
  

  return <div>
      <h1>Вы кликнули {fetchStory.storyInfo} </h1>
      
    </div>   
  
}

export default Item;

































