import { useState, useEffect } from "react"
import React from "react";
import { BrowserRouter as Router, Route, Routes, ReactRouterDOM, Link } from "react-router-dom";
import Item from "./Item";

export default function FetchNews() {

  
  const [stories, setStories] = useState([])
  const [storiess, setStoriess] = useState([])
  const [query, setQuery] = useState("programming")
  const [isLoading, setIsLoading] = useState(true) // loading state

  const useNavigate = ReactRouterDOM.useNavigate;
  const navigate = useNavigate();

  useEffect(() => {
    setIsLoading(true)

    const fetchNews = async () => {

      const api_url_newstories = await 
      fetch (
      "https://hacker-news.firebaseio.com/v0/newstories.json?print=pretty"
      )
      const idStories = await api_url_newstories.json();
      
      var step;
          // выбираем 100 новых новостей
          for (step = 0; step < 100; step++) {
              // Открываем страницу новости
              const api_urlStory = await
              fetch (
              `https://hacker-news.firebaseio.com/v0/item/${idStories[step]}.json?print=pretty"`
              )
              const story = await api_urlStory.json();
              // setStories(story);
              stories.push(story); 
          } 
          setStoriess(stories);
          console.log(stories)
    }

    fetchNews()
    setIsLoading(false)
  }, [query])

  return (
    <>
      <main>
        {isLoading ? (
          <div className="spinner"></div>
        ) : (
          <>
            <section className="grid grid-cols-1 gap-5 p-5 md:grid-cols-2 container mx-auto lg:max-w-4xl">
              {
                storiess.flatMap((item) => {

                const { by, id,  time, title, url } = item

                // конвертируем дату из long в date
                let date = new Date(time*1000);
                let options = { weekday: 'long', day: 'numeric', year: 'numeric', month: 'long' };
                
                return (
                  <article
                    key={id}
                    className="bg-gray-800 rounded p-3 transition-all duration-150"
                  >
                    <Router>
                      <nav>
                        <Link to="/item/:id">
                          <h3 className="font-bold text-white text-lg mb-3">
                             <Route path='/item/:id' element={Item}/>   
                            {title}
                          </h3>  
                        </Link>  
                      </nav>  

                      {/* <button onClick={async event => { navigate(`/item/${id}`); }}>
                          <h3 className="font-bold text-white text-lg mb-3">
                            {title}
                          </h3>
                      </button> */}
                      
                      

                      <Routes>
                        <Route path="/" element={<FetchNews />} />
                        <Route path='/item/:id' element={Item}/>
                      </Routes>
                    </Router>
                    
                    <article className="flex items-center justify-between">
                      <p className="text-gray-600">
                        By <em>{by}</em>
                      </p>
                      <a
                        href={url}
                        target="_blank"
                        rel="noopenner noreferrer"
                        className="border-b border-gray-700 text-gray-600 text-lg hover:text-gray-400 hover:border-gray-400"
                      >
                        Read More
                      </a>
                    </article>
                    <p className="text-gray-400 mt-10">
                      {date.toLocaleDateString("en-US", options)}
                    </p>
                  </article>
                )
              })}
            </section>
          </>
        )}
      </main>
    </>
  )
}
