import { id } from "date-fns/locale";
import { BrowserRouter as Router, Route, Routes, ReactRouterDOM, Link, Outlet } from "react-router-dom";
import { useState, useEffect } from "react"

// export default function Item() {

//         return (
//             <div>
//                 Hello world
//             </div>
//         )

// }

export default function Item() {

    const currentUrl = window.location.href;
    const localhost3000 = "http://localhost:3000/Item/"
    const itemId = currentUrl.replace(localhost3000, "")
    const [query, setQuery] = useState("programming")
    const [isLoading, setIsLoading] = useState(true) // loading state
  
    const [storiesss, setStoriesss] = useState([])

    useEffect(() => {
        setIsLoading(true)
        const fetchNews = async () => {
            const api_story_id = await 
            fetch (`https://hacker-news.firebaseio.com/v0/item/${itemId}.json?print=pretty`)
            const storyInfo = await api_story_id.json();
            console.log(storyInfo)
            storiesss.push(storyInfo)
            console.log(" story1 " + storiesss)
        }
        fetchNews()
        
        setIsLoading(false)
    }, [query])  
    
    return (


<>
<main>
  {isLoading ? (
    <div className="spinner"></div>
  ) : (
    <>
      <section className="grid grid-cols-1 gap-5 p-5 md:grid-cols-2 container mx-auto lg:max-w-4xl">
        {
          storiesss.flatMap((item) => {

        //   const { by, descendants, id,  kids, score, time, title, type, url } = item
          
          const { by, id, score, time,  url } = item

          // конвертируем дату из long в date
          let date = new Date(time*1000);
          let options = { weekday: 'long', day: 'numeric', year: 'numeric', month: 'long' };

          console.log("jsdgjisdinijiisdogjisodg")
          

          return (
            <article
              key={id}
              className="bg-gray-800 rounded p-3 transition-all duration-150"
            >

                <Link to="/">
                    <h3 className="font-bold text-white text-lg mb-3">Back</h3>
                </Link>
        
                <Outlet />

                
              <article className="flex items-center justify-between">
                <p className="text-gray-600">
                  By <em>{by}</em>
                </p>
                <a
                  href={url}
                  target="_blank"
                  rel="noopenner noreferrer"
                  className="border-b border-gray-700 text-gray-600 text-lg hover:text-gray-400 hover:border-gray-400"
                >
                  Read More
                </a>
              </article>
              <p className="text-gray-400 mt-10">
                {date.toLocaleDateString("en-US", options)}
              </p>
            </article>
          )
        })}
      </section>
    </>
  )}
</main>
</>
)
    
}

export {Item}
