import { useState, useEffect } from "react"
// import { Route } from "react-router-dom"
import React from "react";
// import { BrowserRouter as Router, Route, Routes, ReactRouterDOM } from "react-router-dom";
// import format from "date-fns/format"
// You can use the import above or the one below
// import { format } from "date-fns"
// import { da } from "date-fns/locale"

export default function FetchNews() {

  const [items, setItems] = useState([])
  
  const [stories, setStories] = useState([])
  const [storiess, setStoriess] = useState([])
  const [query, setQuery] = useState("programming")
  // const [text, setText] = useState("")
  const [largeTitle, setLargeTitle] = useState([])
  const [isLoading, setIsLoading] = useState(true) // loading state

  const useNavigate = ReactRouterDOM.useNavigate;
  const navigate = useNavigate();

  useEffect(() => {
    setIsLoading(true)

    const fetchNews = async () => {
        


    //   const url = `https://hn.algolia.com/api/v1/search?query=${query}`
      const url = `http://hn.algolia.com/api/v1/search_by_date?tags=story`
      
      const res = await fetch(url)
      const data = await res.json()
      // You can change the number of items you get back in your response using
      // the `Array.length` method, as demonstrated below. Uncomment the line and
      // reload your app to see it in action.
    //   // data.hits.length = 10
      console.log(data)
      setItems(data.hits)
      setLargeTitle(data.hits[0])

      const api_url_newstories = await 
      fetch (
      "https://hacker-news.firebaseio.com/v0/newstories.json?print=pretty"
      )
      const idStories = await api_url_newstories.json();
      
      var step;
          // выбираем 100 новых новостей
          for (step = 0; step < 100; step++) {
              // Открываем страницу новости
              const api_urlStory = await
              fetch (
              `https://hacker-news.firebaseio.com/v0/item/${idStories[step]}.json?print=pretty"`
              )
              const story = await api_urlStory.json();
              // setStories(story);
              stories.push(story); 
          } 
          setStoriess(stories);
          console.log(stories)
    }

    fetchNews()
    setIsLoading(false)
  }, [query])

  const handleSubmit = async (e) => {
    e.preventDefault();
    // console.log(items)
  }

  return (
    <>
      <main>
        {isLoading ? (
          <div className="spinner"></div>
        ) : (
          <>
            {/* Search form */}
            <form
              onSubmit={handleSubmit}
              className="flex place-items-center container mx-auto lg:max-w-4xl mt-10 px-5"
            >
              {/* <input
                type="text"
                name="text"
                id="text"
                placeholder="Search for something..."
                autoComplete="off"
                required
                value={text}
                onChange={(e) => setText(e.target.value)}
                className="w-full py-2 px-4 rounded bg-transparent border border-gray-700 focus:border-gray-600 transition-all duration-150 outline-none text-gray-700 placeholder-gray-700 text-xl lg:text-4xl lg:pb-4 mr-5"
              /> */}
              <button
                type="submit"
                onClick={handleSubmit}
                className="bg-white border border-gray-700 text-xl lg:text-4xl py-2 px-6 rounded lg:pb-4 text-gray-700 hover:bg-transparent transition-all duration-150"
              >
                Search
              </button>
            </form>
            {/* End of search form */}

            {/* <article className="my-10 flex flex-col items-center justify-center container lg:max-w-4xl mx-auto px-5">
              <h1 className="font-bold text-center text-4xl my-5 text-white lg:text-6xl">
                {largeTitle.title}
              </h1>
              <a
                href={largeTitle.url}
                target="_blank"
                rel="noreferrer"
                className="border-b border-gray-700 text-gray-600 text-lg hover:text-gray-400 hover:border-gray-400"
              >
                Read Full Story
              </a>
            </article> */}

            {/* <article className="container mx-auto lg:max-w-4xl px-5">
              <p className="text-gray-600">
                Category:{" "}
                <span className="font-bold text-gray-400 capitalize">
                  {query}
                </span>
              </p>
            </article> */}

            <section className="grid grid-cols-1 gap-5 p-5 md:grid-cols-2 container mx-auto lg:max-w-4xl">
              {
              // items.map((item) => {
                storiess.flatMap((item) => {
                // const { author, created_at, objectID, title, url } = item   //старое
                const { by, id,  time, title, url } = item
                console.log("gdfdsg" + stories.toString )
                // конвертируем дату из long в date
                let date = new Date(time*1000);
                let options = { weekday: 'long', day: 'numeric', year: 'numeric', month: 'long' };
                
                return (
                  <article
                    key={id}
                    // key={id}
                    className="bg-gray-800 rounded p-3 transition-all duration-150"
                  >
                    {/* <Router>
                      <Routes> */}
                        <h3 className="font-bold text-white text-lg mb-3">
                          {/* <Route path='/{title}' element={title}/>  
                           */}
                          {title}
                        </h3>
                      {/* </Routes>
                    </Router> */}
                    
                    <article className="flex items-center justify-between">
                      <p className="text-gray-600">
                        By <em>{by}</em>
                      </p>
                      <a
                        href={url}
                        target="_blank"
                        rel="noopenner noreferrer"
                        className="border-b border-gray-700 text-gray-600 text-lg hover:text-gray-400 hover:border-gray-400"
                      >
                        Read More
                      </a>
                    </article>
                    <p className="text-gray-400 mt-10">
                      {/* Format date using the `format` method from `date-fns` */}
                      {/* {format(new Date(created_at), "dd MMM yyyy")} */}
                      {date.toLocaleDateString("en-US", options)}
                    </p>
                  </article>
                )
              })}
            </section>
          </>
        )}
      </main>
    </>
  )
}
